import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpClick = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleLoClick = () => {
    let newText = text.toLowerCase();
    setText(newText);
  };
  const handleCleClick = () => {
    let newText = "";
    setText(newText);
  };
  const handleOnChange = (event) => {
    setText(event.target.value);
  };
  const [text, setText] = useState("Enter text here");

  return (
     <div >
      <h1>{props.heading}</h1>

      <div className="mb-3">
        <label htmlFor="floatingTextarea">Enter text to analyse below</label>

        <textarea
          className="form-control mb-3"
          value={text}
          onChange={handleOnChange}
          id="floatingTextarea"
          style={{backgroundColor:props.mode==='light'?'white':'#343a40'}}
          rows="8"
        ></textarea>
      </div>
      <button className="btn btn-primary  " onClick={handleUpClick}>
        Convert to Uppercase
      </button>
      <button className="btn btn-primary mx-2 " onClick={handleLoClick}>
        Convert to Lowercase
      </button>
      <button className="btn btn-primary mx-2 " onClick={handleCleClick}>
        Clear
      </button>
      <h2>Your text summary</h2>
<p> {text.split(" ").length} words {text.length-text.split(" ").length+1} characters</p>
      {text}
    </div>
  );
}
