    }
   const handleUpClick=()=>{
    console.log("Uppercase was clicked");
    let newText = text.toUpperCase();
    setText(newText)
   }
    const [text, setText] = useState('Enter text here');

  return (
    <div>
                <h1>{props.heading}</h1>

    <div className="mb-3">
           
    <label for="floatingTextarea">Enter text to analyse below</label>

  <textarea className="form-control" value={text} onChnage={handleOnChange} placeholder="Leave a comment here" id="floatingTextarea" rows="8"></textarea>

    </div>
